            exact 
            element={<AddContact />} />

          <Route 
            path="/edit" 
            exact 
            element={<EditContact />} />

          <Route path="/contact/:id" element={<ContactDetail/>}/> 
        </Routes>
        {/* <AddContact addContactHandler = {addContactHandler}/>
        <ContactList contacts= {contacts} getContactId={removeContactHandler} /> */}
        </ContactsCrudContextProvider>
      </Router> 
    </div>
  );
}

export default App;
