import React, {useRef, useEffect} from "react";
import { Link } from "react-router-dom";
import { useContactsCrud } from "../context/ContactsCrudContext";
import ContactCard from "./ContactCard";

const ContactList = (props) => {
  const {contacts, retrieveContacts} = useContactsCrud();
  const inputEl = useRef("");
  console.log(props);


  useEffect(() => {
    retrieveContacts();
  },[])

  const renderContactList = contacts.map((contact) => {
    return (
      <ContactCard
        contact={contact}
        clickHander={deleteConactHandler}
        key={contact.id}
      />
    );
  });

  const getSearchTerm = () => {
    props.searchKeyWord(inputEl.current.value); 
  }

  return (
  <div class="main">
    <br></br>
    <h2>Contacts
      <Link to="/add">
        <button className="ui right floated pink button">Create Contact</button>
      </Link>
    </h2>
    <div className="ui search">
      <div className="ui icon input">
        <input ref={inputEl} type="text" placeholder="Search Contacts" className="prompt" value={props.term} onChange={getSearchTerm} />
        <i className="search icon"></i>
      </div>
    </div>
    <div className="ui celled list">{renderContactList}</div>
   </div>
  );
};

export default ContactList;

